import {useEffect, useState }from 'react';
import Axios from "axios";
import './App.css';

function App() {
  const [allMovies,setAllMovies]=useState([])
  useEffect(()=>{
    Axios.get('http://localhost:3009/api/select').then((result)=>{
      setAllMovies(result.data);
    })
  })

  const [movieName,setName]=useState('');
  const [Review,setReview]=useState('');

  const addMovie=(e)=>{
    e.preventDefault();
    Axios.post('http://localhost:3009/api/insert',{movieName:movieName,Review: Review})
    .then(()=>{
      alert("insert succefully");
    })
  }


  return (
    <div className="App">
      <div>
        <h1>CRUD APPLICATION</h1>
        <form>
          <label>Name of Movie :</label>
          <input type="text" onChange={(e)=>{setName(e.target.value)}} />
          <label>Review</label>
          <input type="text" onChange={(e)=>{setReview(e.target.value)}} />
          <button onClick={(e)=>{addMovie(e)}} >add Review</button>
        </form>
      </div>
      <div>
        <h2>All Movies</h2>
        {
          allMovies && allMovies.map((movie)=>{
            return (<p>Name : {movie.nameMovie} Review:{movie.nameMovie}</p>)
          })
        }
      </div>
    </div>
  );
}

export default App;
