import {useEffect, useState }from 'react';
import Axios from "axios";
import 'bootstrap/dist/css/bootstrap.css';
import "bootstrap/dist/js/bootstrap.bundle";
import './App.css';

function App() {
  const [allMovies,setAllMovies]=useState([])
  useEffect(()=>{
    Axios.get('http://localhost:3009/api/select').then((result)=>{
      setAllMovies(result.data);
    })
  })

  const [movieName,setName]=useState('');
  const [Review,setReview]=useState('');

  const addMovie=(e)=>{
    e.preventDefault();
    Axios.post('http://localhost:3009/api/insert',{movieName:movieName,Review: Review})
    .then(()=>{
      alert("insert succefully");
    })
  }


  return (
    <div className="App">

<div class="accordion accordion-flush" id="accordionFlushExample">
  <div class="accordion-item">
    <h2 class="accordion-header">
      <button class="accordion-button collapsed" type="button" data-bs-toggle="collapse" data-bs-target="#flush-collapseOne" aria-expanded="false" aria-controls="flush-collapseOne">
        Accordion Item #1
      </button>
    </h2>
    <div id="flush-collapseOne" class="accordion-collapse collapse" data-bs-parent="#accordionFlushExample">
      <div class="accordion-body">Placeholder content for this accordion, which is intended to demonstrate the <code>.accordion-flush</code> class. This is the first item's accordion body.</div>
    </div>
  </div>
  <div class="accordion-item">
    <h2 class="accordion-header">
      <button class="accordion-button collapsed" type="button" data-bs-toggle="collapse" data-bs-target="#flush-collapseTwo" aria-expanded="false" aria-controls="flush-collapseTwo">
        Accordion Item #2
      </button>
    </h2>
    <div id="flush-collapseTwo" class="accordion-collapse collapse" data-bs-parent="#accordionFlushExample">
      <div class="accordion-body">Placeholder content for this accordion, which is intended to demonstrate the <code>.accordion-flush</code> class. This is the second item's accordion body. Let's imagine this being filled with some actual content.</div>
    </div>
  </div>
  <div class="accordion-item">
    <h2 class="accordion-header">
      <button class="accordion-button collapsed" type="button" data-bs-toggle="collapse" data-bs-target="#flush-collapseThree" aria-expanded="false" aria-controls="flush-collapseThree">
        Accordion Item #3
      </button>
    </h2>
    <div id="flush-collapseThree" class="accordion-collapse collapse" data-bs-parent="#accordionFlushExample">
      <div class="accordion-body">Placeholder content for this accordion, which is intended to demonstrate the <code>.accordion-flush</code> class. This is the third item's accordion body. Nothing more exciting happening here in terms of content, but just filling up the space to make it look, at least at first glance, a bit more representative of how this would look in a real-world application.</div>
    </div>
  </div>
</div>


      <div className="alert alert-warning" role="alert">
        Simple application Using NODEJS and MYSQL
      </div>




      <div>
        <h1>CRUD APPLICATION</h1>
        <form>
          <label>Name of Movie :</label>
          <input type="text" onChange={(e)=>{setName(e.target.value)}} />
          <label>Review : </label>
          <input type="text" onChange={(e)=>{setReview(e.target.value)}} />
          <button className='btn btn-secondary' onClick={(e)=>{addMovie(e)}} >add Review</button>
        </form>
      </div>
      <div className='container'>
        <h2>All Movies</h2>
        <div className="accordion" id="accordionExample">
        {
          allMovies && allMovies.map((movie)=>{
            return (
              <div className="accordion-item">
                <h2 className="accordion-header" id="headingOne">
                  <button className="accordion-button" type="button" data-bs-toggle="collapse" data-bs-target="#collapseOne" aria-expanded="true" aria-controls="collapseOne">
                    Name : {movie.nameMovie}
                  </button>
                </h2>
              <div id="collapseOne" className="accordion-collapse collapse show" aria-labelledby="headingOne" data-bs-parent="#accordionExample">
                <div className="accordion-body">
                  <strong> Review:{movie.reviewMovie}</strong> 
                </div>
              </div>
            </div>
            )
          })
        }
        </div>
      </div>
    </div>
  );
}

export default App;
